package agent

import "time"

// BrokerEnvelope is the envelope for Kafka message
// RemoteAddr is the property for httpRequest.RemoteAddr
// If a proxy server was used, the header value for 'x-forwarded-for' will be used to resolve RemoteAddr
// RegID was used before EndpointID become the official term.
// Hence LegacyRegID is named such for the value of RegID generated by legacy system
type BrokerEnvelope struct {
	Action            string
	Topic             string
	RegID             string
	PartnerID         string
	SiteID            string
	ClientID          string
	ProbeEndpointID   string
	EndpointID        string
	EndpointType      string
	AgentInstalledUTC time.Time
	AgentID           string
	LegacyRegID       string
	DcDateTimeUtc     time.Time
	RemoteAddr        string
	TransactionID     string
	Checksum          string
	OSName            string
	OSType            string
	OSVersion         string
}
