package agent

import "time"

// Download status
const (
	DownloadStatusSuccess = "Success"
	DownloadStatusFailed  = "Failed"
)

// DownloadResultExpirationError is a message for download process expiration
const DownloadResultExpirationError = "download process expired"

// DownloadResultKafkaMessage structure represents the Kafka message
type DownloadResultKafkaMessage struct {
	BrokerEnvelope
	Message DownloadResultResponseMessage `json:"message"`
}

// DownloadResultResponseMessage download response message
type DownloadResultResponseMessage struct {
	ExecutionID  string    `json:"executionID" description:"ExecutionID generated by MicroService"`
	Status       string    `json:"status" description:"Return status: Success/Failed"`
	Error        string    `json:"error" description:"Error during download"`
	FilePath     string    `json:"filePath" description:"Downloaded file path"`
	TimestampUTC time.Time `json:"timestampUTC" description:"UTC time"`
	Metadata     string    `json:"metadata" description:"Metadata which automatically populated from input message Metadata field"`
}
