package patching

import "time"

// InstallInputMessage is a definition of /resources/patching/patchingInstallInputMessage.json
type InstallInputMessage struct {
	ExecutionID string       `json:"executionID" description:"ExecutionID generated by Patching MicroService"`
	Script      PluginScript `json:"script" description:"Script that must be executed"`
	FilePath    string       `json:"filePath" description:"Path to downloaded file"`
	Metadata    string       `json:"metadata" description:"Metadata which automatically populated from input message Metadata field"`
	ExpiredAt   time.Time    `json:"expiredAt"`
}

// PluginScript is a definition of /resources/patching/patchingPluginScript.json
type PluginScript struct {
	Executor string `json:"executor" description:"Interpreter name (e.g. powershell)"`
	Body     string `json:"body" description:"Sequence of instructions"`
}
