package tasking

// ScriptPluginInputMessage is a definition of /resources/taskScriptEngine/scriptPluginInputMessage.json
type ScriptPluginInputMessage struct {
	ExecutionID string             `json:"executionID" description:"ExecutionID generated by Scripting MicroService"`
	OriginID    string             `json:"originID"    description:"OriginID is a predefined script id"`
	Script      ScriptPluginScript `json:"script"      description:"Script that must be executed"`
}

// ScriptPluginScript is a definition of /resources/taskScriptEngine/scriptPluginScript.json
type ScriptPluginScript struct {
	Executor                 string `json:"executor"                  description:"Script that must be executed"`
	ExecutorVersion          int    `json:"executorVersion"           description:"Major version of executor that is supported"`
	ExpectedExecutionTimeSec int    `json:"expectedExecutionTimeSec"  description:"Oriented time of execution"`
	Body                     string `json:"body"                      description:"Sequence of instructions"`
	OutputFormatType         string `json:"outputFormatType"          description:"Format of script output"`
	SendIdenticalCounter     int    `json:"sendIdenticalCounter"      description:"Count of identical script outputs"`
	SuppressIdenticalCounter int    `json:"suppressIdenticalCounter"  description:"Count of suppressed script outputs"`
}
