package tasking

import "time"

// ScriptPluginReturnMessage is a definition of /resources/taskScriptEngine/scriptPluginReturnMessage.json
type ScriptPluginReturnMessage struct {
	ExecutionID  string      `json:"executionID"  description:"ExecutionID generated by Scripting MicroService"`
	OriginID     string      `json:"originID"     description:"OriginID is a predefined script id"`
	Status       string      `json:"status"       description:"Return status: Success/Failed"`
	Stdout       string      `json:"stdout"       description:"Script's STDOUT"`
	Stderr       string      `json:"stderr"       description:"Blank if status is Success or Script's STDERR otherwise"`
	Result       interface{} `json:"result"       description:"Use like JSON interpratation of STDOUT"`
	TimestampUTC time.Time   `json:"timestampUTC" description:"UTC time when the Script execution finished"`
}
