$Data1 = @()
$stdout = @()

                $result = query session /server:$env:COMPUTERNAME
                $rows = $result -split "`n"            
                foreach ($row in $rows) {                
                $regex = "Disc|Active"
                    
                    if ($row -NotMatch "services" -and $row -match $regex) {
                        $session = ($($row -Replace ' {2,}', ',').split(','))           

                    if($session[3] -eq 'Disc'){
                    $session[3] = 'Disconnected'
                    }
                    
                    $Data = new-object psobject -Property @{
                    #COMPUTERNAME = $env:COMPUTERNAME
                    "Session Name" = $session[0]
                    "USERNAME" = $session[1]
                    "SessionID" = $session[2]
                    "STATE" = $session[3]
                   }
                
                $Data1 += $Data

                $stdout += @("Session Name: $($Data.'Session Name')`r`nUSERNAME: $($Data.USERNAME)`r`nSessionID: $($Data.SessionID)`r`nSTATE: $($Data.STATE)")
                
                }

                }

