function Get_stderr
{
param($Title,$details)
$info = @()
$ID = 0

 $details | %{
    $info += new-object psobject -Property @{
    "id" = $ID
    "title" = $Title
    "detail" = $_
    }
    $ID++
    }

return $info
}

function Get_DataObject
{
param($Title,$details)
$info = @()

 $details | %{
    $info += new-object psobject -Property @{
    "title" = $Title
    "Status" = $_
    }
    $ID++
    }
return $info
}

